import java.sql.Connection;
import java.sql.DriverManager;
import java.sql.SQLException;

public class DatabaseExample {
    public static void main(String[] args) {
        String url = "jdbc:mysql://localhost:3306/mydatabase";
        String user = "myuser";
        String password = "mypass";

        try (Connection conn = DriverManager.getConnection(url, user, password)) {
            int id = 5; // Dummy ID for demonstration purposes
            String query = "SELECT * FROM users WHERE id = ?";

            // Use a PreparedStatement to safely execute the query
            PreparedStatement statement = conn.prepareStatement(query);
            statement.setObject(1, id); // Set the value of the parameter

            // Execute the prepared statement
            ResultSet resultSet = statement.executeQuery();

            // Process the result set
            while (resultSet.next()) {
                System.out.println("User ID: " + resultSet.getString("id") + ", Username: " + resultSet.getString("username"));
            }
        } catch (SQLException e) {
            e.printStackTrace();
        }
    }
}
```

